import React, { useState } from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import { useParams } from "react-router";

const GET_JOB = gql`
  query getJob($companySlug: String!, $jobSlug: String!) {
    job(input: { companySlug: $companySlug, jobSlug: $jobSlug }) {
      id
      title
      isPublished
      description
      company {
        name
      }
      userEmail
      applyUrl
    }
  }
`;

export default function JobDetails(props) {
  const { job_slug, company_slug } = useParams();
  let [job, setJob] = useState({});
  const { loading, data } = useQuery(GET_JOB, {
    variables: {
      jobSlug: job_slug,
      companySlug: company_slug
    }
  });
  useEffect(() => {
    if(data){
    
    setJobs(data.jobs);

  }
    
    
  }, [data])
  if (loading) {
    return (
      
      <div className="">
  
            <h4 className="">Job Details </h4>
            <div className="">
              <h3>Loading...</h3>
            </div>
            </div>
          
    );
  }
  const { id, title, isPublished, description, company, applyUrl } = job;
  return (
    <div class="">
      <div class="">
        <div class="">
          <h4 class="">Job Details</h4>
          <div class="">
            <div class="">
              <div class=""></div>
              <div class="">
                <input
                  type="checkbox"
                  class="custom-control-input"
                  id={id}
                  checked={isPublished}
                />
                <label
                  class=""
                  for={id}
                  title="Published"
                ></label>
                <a
                  href="javascript:void(0)"
                  class="btn btn-sm text-info font-18"
                >
                  {title}
                </a>
                <small>at </small>{" "}
                {company && (
                  <b className="text-danger font-16">{company.name}</b>
                )}
              </div>
              <p class="">{description}</p>
              <p class="">
                <a
                  href={applyUrl}
                  rel="noopener noreferrer"
                  target="_blank"
                  class=""
                >
                  Apply
                </a>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
