import React, { useState,useEffect } from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
import "../style/base/base.css";
import "../style/component/jobs.css";



// GQ Query to fetch all the JOBS
const JOBS = gql`
  {
    jobs {
      id
      title
      isPublished
      description
      slug
      company {
        name
        slug
      }
      userEmail
      applyUrl
    }
  }
`;

export default function Job(props) {

  let [jobs, setJobs] = useState([]);
  const { loading, data } = useQuery(JOBS);
  useEffect(() => {
    if(data){

    setJobs(data.jobs);
  }


  }, [data])
  if (loading)
    return (
     <div className="loading">
     <h1>loading....</h1>
      </div>
    );



  return (
   <div>
    <input

            type="text"
            className="form"
            placeholder="Enter Job or Company for serch"
            //onChange={onChange}
          />


     {jobs.map(function(item, i){
       console.log(item,'index i',i);
  return <div className="main_div">

  <div className="data">
  <input
                        type="checkbox"
                        class="custom-control-input"
                        id={item.id}
                        checked={item.isPublished}
                      />
                      <label
                        class="custom-control-label"
                        for={item.id}
                        title="Published"
                      ></label>
                      {item.company && (
                        <a
                          href={`/jobs/${item.slug}/${item.company.slug}`}
                          class="btn btn-sm text-info font-18"
                        >
                          {item.title}
                        </a>

                      )}
                      <small>at </small>{" "}
                      <b className="text-danger font-16">{item.company.name}</b>





  </div>
  </div>
})}
   </div>
  );
}
