import React, { useState,useEffect } from "react";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";

// GQ Query to fetch all the JOBS
const JOBS = gql`
  {
    jobs {
      id
      title
      isPublished
      description
      slug
      company {
        name
        slug
      }
      userEmail
      applyUrl
    }
  }
`;

export default function Job(props) {

  let [jobs, setJobs] = useState([]);

  const { loading, data } = useQuery(JOBS);
  const onChange = e => {
    let j = jobs.filter(j =>
        j.title.toLowerCase().includes(e.target.value.toLowerCase()) ||
        j.company.name.toLowerCase().includes(e.target.value.toLowerCase())
    );

  };
  useEffect(() => {

  if(data){
    console.log(data,'data responsed');
    setJobs(data.jobs);
  }


  }, [data])
  if (loading)
    return (
     <div>
     <h1>loading....</h1>
      </div>
    );
  // set state when API returns record the first time. will be called only once


  return (
   <div>
     <p>joblist data{jobs.length}</p>
     {jobs.map(function(item, i){

